import { Traits } from './types';

/**
 * Make sure the object key perfectly matches the attribute value.
 * Each index of the rarity array corresponds with the trait value.
 * An array of zeros means that there is no weight for this trait and the odds are neutral.
 *
 * @link https://chancejs.com/miscellaneous/weighted.html
 *
 * @todo We need to figure out and come up with a name for each possible Trait
 * (Arm types, chest types, etc)
 */

export const bg: Traits['background'][] = ['red', 'yellow', 'pink', 'purple'];
export const bg_rarity = [0, 1, 1, 2];

export const base: Traits['base'][] = ['Devie', 'Johnny', 'Maggie', 'Swervie'];
export const base_rarity = [0, 0, 0, 0];

export const color: Traits['color'][] = ['gray'];
export const color_rarity = [0, 0];

export const arm: Traits['arms'][] = ['gray', 'red'];
export const arm_rarity = [0, 0];

export const chest: Traits['chest'][] = ['gray', 'red'];
export const chest_rarity = [0, 0];
